import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";

const days = [
  { name: "Saturday", date: "01/04/2025" },
  { name: "Sunday", date: "01/05/2025" },
  { name: "Monday", date: "01/06/2025" },
  { name: "Tuesday", date: "01/07/2025" },
  { name: "Wednesday", date: "01/08/2025" },
  { name: "Thursday", date: "01/09/2025" },
  { name: "Friday", date: "01/10/2025" }
];

const jobColors = {
  "MILLEMORE SIGMA - 243962": "#FFD700",
  "CAPSTONE AC03 - 233831": "#87CEFA",
  "ASH MULTI BLDG": "#90EE90"
};

const initialJobs = {
  "Leo Garcia": [{ job: "MILLEMORE SIGMA - 243962", time: "7:00 AM - 3:30 PM" }, {}, {}, {}, {}, {}, {}],
  "Daniel Hitt": [{}, {}, { job: "CAPSTONE AC03 - 233831", time: "7:00 AM - 3:30 PM" }, { job: "CAPSTONE AC03 - 233831", time: "7:00 AM - 3:30 PM" }, { job: "CAPSTONE AC03 - 233831", time: "7:00 AM - 3:30 PM" }, { job: "ASH MULTI BLDG", time: "7:00 AM - 3:30 PM" }, { job: "CAPSTONE AC03 - 233831", time: "7:00 AM - 3:30 PM" }],
  "Maria Lopez": [{}, {}, { job: "ASH MULTI BLDG", time: "7:00 AM - 3:30 PM" }, { job: "ASH MULTI BLDG", time: "7:00 AM - 3:30 PM" }, {}, {}, {}]
};

export default function ScheduleGrid() {
  const [team, setTeam] = useState(Object.keys(initialJobs));
  const [jobs, setJobs] = useState(initialJobs);

  return (
    <div className="p-4 space-y-4">
      <h1 className="text-2xl font-bold">Weekly Team Schedule</h1>
      <div className="grid grid-cols-8 gap-2 items-start">
        <div className="font-semibold text-center min-h-[72px] flex items-center justify-center bg-gray-100 rounded p-2">
          Team Member
        </div>
        {days.map((day, i) => (
          <div key={i} className="text-center bg-gray-100 rounded p-2 min-h-[72px]">
            <div className="font-semibold whitespace-nowrap">{day.name}</div>
            <div className="text-xs text-gray-500">{day.date}</div>
          </div>
        ))}
        {team.map((member, i) => (
          <React.Fragment key={i}>
            <div className="font-medium text-sm text-center col-span-1 bg-gray-100 rounded p-2 min-h-[72px] flex items-center justify-center">
              {member}
            </div>
            {jobs[member].map((entry, j) => (
              <Card
                key={j}
                className="min-h-[72px] flex items-center justify-center"
                style={{ backgroundColor: jobColors[entry.job] || "#f9f9f9" }}
              >
                <CardContent className="p-2 text-xs text-center w-full whitespace-nowrap">
                  {entry.job ? (
                    <>
                      <div className="font-semibold text-[11px] truncate w-full">{entry.job}</div>
                      <div className="text-[10px] text-gray-700">{entry.time}</div>
                    </>
                  ) : (
                    <Button size="sm" variant="ghost">+</Button>
                  )}
                </CardContent>
              </Card>
            ))}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}
