import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";

const initialTeam = ["Leo Garcia", "Daniel Hitt", "Maria Lopez"];

export default function TeamManagement() {
  const [team, setTeam] = useState(initialTeam);
  const [newMember, setNewMember] = useState("");

  const handleAddMember = () => {
    if (newMember.trim() && !team.includes(newMember)) {
      setTeam([...team, newMember]);
      setNewMember("");
    }
  };

  const handleRemoveMember = (member) => {
    setTeam(team.filter(m => m !== member));
  };

  return (
    <div className="p-4 space-y-4">
      <h1 className="text-2xl font-bold">Team Management</h1>
      <div className="flex gap-2 items-center">
        <Input
          placeholder="Add team member"
          value={newMember}
          onChange={(e) => setNewMember(e.target.value)}
        />
        <Button onClick={handleAddMember}>Add</Button>
      </div>
      <ul className="space-y-2">
        {team.map((member, index) => (
          <li key={index} className="flex justify-between items-center bg-gray-100 p-2 rounded">
            <span>{member}</span>
            <Button size="sm" variant="ghost" className="text-red-500" onClick={() => handleRemoveMember(member)}>
              Remove
            </Button>
          </li>
        ))}
      </ul>
    </div>
  );
}
